import React, { useState } from 'react';

function App() {
  const [sales, setSales] = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [saleInput, setSaleInput] = useState('');
  const [expenseInput, setExpenseInput] = useState('');

  const addSale = () => {
    if (saleInput) setSales([...sales, parseInt(saleInput)]);
    setSaleInput('');
  };

  const addExpense = () => {
    if (expenseInput) setExpenses([...expenses, parseInt(expenseInput)]);
    setExpenseInput('');
  };

  const totalSales = sales.reduce((a, b) => a + b, 0);
  const totalExpenses = expenses.reduce((a, b) => a + b, 0);
  const netProfit = totalSales - totalExpenses;

  return (
    <div className="p-8 font-sans max-w-md mx-auto">
      <h1 className="text-2xl font-bold mb-4">NA BAKERY üç∞</h1>

      <div className="mb-6">
        <h2 className="font-semibold">Tambah Penjualan</h2>
        <input
          type="number"
          value={saleInput}
          onChange={(e) => setSaleInput(e.target.value)}
          className="border p-1 mr-2"
        />
        <button onClick={addSale} className="bg-green-500 text-white px-3 py-1 rounded">Simpan</button>
      </div>

      <div className="mb-6">
        <h2 className="font-semibold">Tambah Pengeluaran</h2>
        <input
          type="number"
          value={expenseInput}
          onChange={(e) => setExpenseInput(e.target.value)}
          className="border p-1 mr-2"
        />
        <button onClick={addExpense} className="bg-red-500 text-white px-3 py-1 rounded">Simpan</button>
      </div>

      <div>
        <h2 className="font-semibold mb-2">Ringkasan</h2>
        <p>Total Penjualan: Rp {totalSales}</p>
        <p>Total Pengeluaran: Rp {totalExpenses}</p>
        <p className="font-bold">Laba Bersih: Rp {netProfit}</p>
      </div>
    </div>
  );
}

export default App;
