import React, { useState } from "react";
import jsPDF from "jspdf";
import "jspdf-autotable";

function App() {
  const [stok, setStok] = useState([
    { nama: "Roti Tawar", jumlah: 50, harga: 5000 },
    { nama: "Donat", jumlah: 30, harga: 7000 },
  ]);

  const [penjualan, setPenjualan] = useState([]);
  const [pengeluaran, setPengeluaran] = useState([]);

  const [inputPenjualan, setInputPenjualan] = useState({
    nama: "",
    jumlah: 0,
  });
  const [inputPengeluaran, setInputPengeluaran] = useState({
    nama: "",
    biaya: 0,
  });

  // Tambah penjualan
  const handleTambahPenjualan = () => {
    const itemStok = stok.find((b) => b.nama === inputPenjualan.nama);
    if (!itemStok) {
      alert("Barang tidak ada di stok!");
      return;
    }
    if (inputPenjualan.jumlah > itemStok.jumlah) {
      alert("Stok tidak cukup!");
      return;
    }
    const total = itemStok.harga * inputPenjualan.jumlah;
    setPenjualan([...penjualan, { ...inputPenjualan, harga: itemStok.harga, total }]);
    setStok(
      stok.map((b) =>
        b.nama === inputPenjualan.nama
          ? { ...b, jumlah: b.jumlah - inputPenjualan.jumlah }
          : b
      )
    );
    setInputPenjualan({ nama: "", jumlah: 0 });
  };

  // Hapus penjualan
  const handleHapusPenjualan = (index) => {
    const pj = penjualan[index];
    setStok(
      stok.map((b) =>
        b.nama === pj.nama ? { ...b, jumlah: b.jumlah + pj.jumlah } : b
      )
    );
    setPenjualan(penjualan.filter((_, i) => i !== index));
  };

  // Edit penjualan
  const handleEditPenjualan = (index) => {
    const pj = penjualan[index];
    const baru = prompt(
      `Ubah jumlah penjualan ${pj.nama}:`,
      pj.jumlah
    );
    const jumlahBaru = parseInt(baru, 10);
    if (isNaN(jumlahBaru) || jumlahBaru < 0) return;
    const itemStok = stok.find((b) => b.nama === pj.nama);
    const selisih = jumlahBaru - pj.jumlah;
    if (selisih > itemStok.jumlah) {
      alert("Stok tidak cukup!");
      return;
    }
    setPenjualan(
      penjualan.map((p, i) =>
        i === index ? { ...p, jumlah: jumlahBaru, total: jumlahBaru * p.harga } : p
      )
    );
    setStok(
      stok.map((b) =>
        b.nama === pj.nama ? { ...b, jumlah: b.jumlah - selisih } : b
      )
    );
  };

  // Tambah pengeluaran
  const handleTambahPengeluaran = () => {
    setPengeluaran([...pengeluaran, inputPengeluaran]);
    setInputPengeluaran({ nama: "", biaya: 0 });
  };

  // Hapus pengeluaran
  const handleHapusPengeluaran = (index) => {
    setPengeluaran(pengeluaran.filter((_, i) => i !== index));
  };

  // Edit pengeluaran
  const handleEditPengeluaran = (index) => {
    const pe = pengeluaran[index];
    const baru = prompt(
      `Ubah biaya pengeluaran ${pe.nama}:`,
      pe.biaya
    );
    const biayaBaru = parseInt(baru, 10);
    if (isNaN(biayaBaru) || biayaBaru < 0) return;
    setPengeluaran(
      pengeluaran.map((p, i) => (i === index ? { ...p, biaya: biayaBaru } : p))
    );
  };

  const totalPenjualan = penjualan.reduce((a, b) => a + b.total, 0);
  const totalPengeluaran = pengeluaran.reduce((a, b) => a + b.biaya, 0);
  const labaBersih = totalPenjualan - totalPengeluaran;

  const handleCetak = () => {
    const doc = new jsPDF();
    doc.text("NA BAKERY - Ringkasan", 10, 10);

    // Penjualan
    doc.text("Penjualan:", 10, 20);
    doc.autoTable({
      startY: 25,
      head: [["Nama Barang", "Harga", "Jumlah", "Total"]],
      body: penjualan.map((p) => [p.nama, p.harga, p.jumlah, p.total]),
    });

    // Pengeluaran
    doc.text("Pengeluaran:", 10, doc.lastAutoTable.finalY + 10);
    doc.autoTable({
      startY: doc.lastAutoTable.finalY + 15,
      head: [["Nama Pengeluaran", "Biaya"]],
      body: pengeluaran.map((p) => [p.nama, p.biaya]),
    });

    // Total & Laba
    doc.text(
      `Total Penjualan: Rp ${totalPenjualan}`, 10, doc.lastAutoTable.finalY + 15
    );
    doc.text(
      `Total Pengeluaran: Rp ${totalPengeluaran}`, 10, doc.lastAutoTable.finalY + 25
    );
    doc.text(
      `Laba Bersih: Rp ${labaBersih}`, 10, doc.lastAutoTable.finalY + 35
    );

    doc.save("ringkasan.pdf");
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>NA BAKERY üç∞</h1>

      <h2>Stok Barang</h2>
      <table border="1" cellPadding="5">
        <thead>
          <tr>
            <th>Nama</th>
            <th>Harga</th>
            <th>Jumlah</th>
          </tr>
        </thead>
        <tbody>
          {stok.map((b, i) => (
            <tr key={i}>
              <td>{b.nama}</td>
              <td>{b.harga}</td>
              <td>{b.jumlah}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Tambah Penjualan</h2>
      <select
        value={inputPenjualan.nama}
        onChange={(e) =>
          setInputPenjualan({ ...inputPenjualan, nama: e.target.value })
        }
      >
        <option value="">Pilih Barang</option>
        {stok.map((b, i) => (
          <option key={i} value={b.nama}>
            {b.nama}
          </option>
        ))}
      </select>
      <input
        type="number"
        placeholder="Jumlah"
        value={inputPenjualan.jumlah}
        onChange={(e) =>
          setInputPenjualan({ ...inputPenjualan, jumlah: parseInt(e.target.value) })
        }
      />
      <button onClick={handleTambahPenjualan}>Simpan</button>

      <h3>Ringkasan Penjualan</h3>
      <table border="1" cellPadding="5">
        <thead>
          <tr>
            <th>Nama Barang</th>
            <th>Harga</th>
            <th>Jumlah</th>
            <th>Total</th>
            <th>Aksi</th>
          </tr>
        </thead>
        <tbody>
          {penjualan.map((p, i) => (
            <tr key={i}>
              <td>{p.nama}</td>
              <td>{p.harga}</td>
              <td>{p.jumlah}</td>
              <td>{p.total}</td>
              <td>
                <button onClick={() => handleEditPenjualan(i)}>Edit</button>
                <button onClick={() => handleHapusPenjualan(i)}>Hapus</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Tambah Pengeluaran</h2>
      <input
        type="text"
        placeholder="Nama Pengeluaran"
        value={inputPengeluaran.nama}
        onChange={(e) =>
          setInputPengeluaran({ ...inputPengeluaran, nama: e.target.value })
        }
      />
      <input
        type="number"
        placeholder="Biaya"
        value={inputPengeluaran.biaya}
        onChange={(e) =>
          setInputPengeluaran({ ...inputPengeluaran, biaya: parseInt(e.target.value) })
        }
      />
      <button onClick={handleTambahPengeluaran}>Simpan</button>

      <h3>Ringkasan Pengeluaran</h3>
      <table border="1" cellPadding="5">
        <thead>
          <tr>
            <th>Nama Pengeluaran</th>
            <th>Biaya</th>
            <th>Aksi</th>
          </tr>
        </thead>
        <tbody>
          {pengeluaran.map((p, i) => (
            <tr key={i}>
              <td>{p.nama}</td>
              <td>{p.biaya}</td>
              <td>
                <button onClick={() => handleEditPengeluaran(i)}>Edit</button>
                <button onClick={() => handleHapusPengeluaran(i)}>Hapus</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Total</h2>
      <p>Total Penjualan: Rp {totalPenjualan}</p>
      <p>Total Pengeluaran: Rp {totalPengeluaran}</p>
      <p>Laba Bersih: Rp {labaBersih}</p>

      <button onClick={handleCetak}>Cetak PDF</button>
    </div>
  );
}

export default App;
