import React, { useState } from "react";

function App() {
  const [penjualan, setPenjualan] = useState([]);
  const [pengeluaran, setPengeluaran] = useState([]);

  const [namaBarang, setNamaBarang] = useState("");
  const [hargaBarang, setHargaBarang] = useState("");
  const [jumlahBarang, setJumlahBarang] = useState("");

  const [namaPengeluaran, setNamaPengeluaran] = useState("");
  const [jumlahPengeluaran, setJumlahPengeluaran] = useState("");

  const [editPenjualanIndex, setEditPenjualanIndex] = useState(null);
  const [editPengeluaranIndex, setEditPengeluaranIndex] = useState(null);

  // Hitung total realtime untuk form penjualan
  const totalRealtime = () => {
    const h = parseInt(hargaBarang) || 0;
    const j = parseInt(jumlahBarang) || 0;
    return h * j;
  };

  // Tambah / Edit Penjualan
  const simpanPenjualan = () => {
    if (!namaBarang || !hargaBarang || !jumlahBarang) return;
    const total = totalRealtime();
    const item = { nama: namaBarang, harga: hargaBarang, jumlah: jumlahBarang, total };

    if (editPenjualanIndex !== null) {
      const updated = [...penjualan];
      updated[editPenjualanIndex] = item;
      setPenjualan(updated);
      setEditPenjualanIndex(null);
    } else {
      setPenjualan([...penjualan, item]);
    }

    setNamaBarang(""); setHargaBarang(""); setJumlahBarang("");
  };

  const editPenjualan = (index) => {
    const item = penjualan[index];
    setNamaBarang(item.nama);
    setHargaBarang(item.harga);
    setJumlahBarang(item.jumlah);
    setEditPenjualanIndex(index);
  };

  const hapusPenjualan = (index) => {
    const updated = penjualan.filter((_, i) => i !== index);
    setPenjualan(updated);
    if (editPenjualanIndex === index) setEditPenjualanIndex(null);
  };

  // Tambah / Edit Pengeluaran
  const simpanPengeluaran = () => {
    if (!namaPengeluaran || !jumlahPengeluaran) return;
    const item = { nama: namaPengeluaran, jumlah: parseInt(jumlahPengeluaran) };

    if (editPengeluaranIndex !== null) {
      const updated = [...pengeluaran];
      updated[editPengeluaranIndex] = item;
      setPengeluaran(updated);
      setEditPengeluaranIndex(null);
    } else {
      setPengeluaran([...pengeluaran, item]);
    }

    setNamaPengeluaran(""); setJumlahPengeluaran("");
  };

  const editPengeluaran = (index) => {
    const item = pengeluaran[index];
    setNamaPengeluaran(item.nama);
    setJumlahPengeluaran(item.jumlah);
    setEditPengeluaranIndex(index);
  };

  const hapusPengeluaran = (index) => {
    const updated = pengeluaran.filter((_, i) => i !== index);
    setPengeluaran(updated);
    if (editPengeluaranIndex === index) setEditPengeluaranIndex(null);
  };

  const totalPenjualan = penjualan.reduce((acc, item) => acc + item.total, 0);
  const totalPengeluaran = pengeluaran.reduce((acc, item) => acc + item.jumlah, 0);
  const labaBersih = totalPenjualan - totalPengeluaran;

  return (
    <div className="p-6 font-sans bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-center">NA BAKERY üç∞</h1>

      <div className="grid md:grid-cols-2 gap-6 mb-6">
        {/* Form Penjualan */}
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-xl font-semibold mb-3">Tambah Penjualan</h2>
          <input className="border p-2 w-full mb-2 rounded" type="text" placeholder="Nama Barang" value={namaBarang} onChange={e => setNamaBarang(e.target.value)} />
          <input className="border p-2 w-full mb-2 rounded" type="number" placeholder="Harga Satuan" value={hargaBarang} onChange={e => setHargaBarang(e.target.value)} />
          <input className="border p-2 w-full mb-2 rounded" type="number" placeholder="Jumlah" value={jumlahBarang} onChange={e => setJumlahBarang(e.target.value)} />
          <p className="mb-2">Total: Rp {totalRealtime()}</p>
          <button className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600" onClick={simpanPenjualan}>
            {editPenjualanIndex !== null ? "Update" : "Simpan"}
          </button>
        </div>

        {/* Form Pengeluaran */}
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-xl font-semibold mb-3">Tambah Pengeluaran</h2>
          <input className="border p-2 w-full mb-2 rounded" type="text" placeholder="Nama Pengeluaran" value={namaPengeluaran} onChange={e => setNamaPengeluaran(e.target.value)} />
          <input className="border p-2 w-full mb-2 rounded" type="number" placeholder="Jumlah Biaya" value={jumlahPengeluaran} onChange={e => setJumlahPengeluaran(e.target.value)} />
          <button className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600" onClick={simpanPengeluaran}>
            {editPengeluaranIndex !== null ? "Update" : "Simpan"}
          </button>
        </div>
      </div>

      {/* Ringkasan */}
      <div className="bg-white p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-3">Ringkasan Penjualan</h2>
        <table className="w-full border-collapse border border-gray-300 mb-4">
          <thead>
            <tr className="bg-gray-100">
              <th className="border border-gray-300 p-2">Nama Barang</th>
              <th className="border border-gray-300 p-2">Harga Satuan</th>
              <th className="border border-gray-300 p-2">Jumlah</th>
              <th className="border border-gray-300 p-2">Total</th>
              <th className="border border-gray-300 p-2">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {penjualan.map((item, idx) => (
              <tr key={idx}>
                <td className="border border-gray-300 p-2">{item.nama}</td>
                <td className="border border-gray-300 p-2">Rp {item.harga}</td>
                <td className="border border-gray-300 p-2">{item.jumlah}</td>
                <td className="border border-gray-300 p-2">Rp {item.total}</td>
                <td className="border border-gray-300 p-2 text-center space-x-1">
                  <button className="bg-yellow-500 text-white px-2 py-1 rounded hover:bg-yellow-600" onClick={() => editPenjualan(idx)}>Edit</button>
                  <button className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600" onClick={() => hapusPenjualan(idx)}>Hapus</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <h2 className="text-xl font-semibold mb-3">Ringkasan Pengeluaran</h2>
        <table className="w-full border-collapse border border-gray-300 mb-4">
          <thead>
            <tr className="bg-gray-100">
              <th className="border border-gray-300 p-2">Nama Pengeluaran</th>
              <th className="border border-gray-300 p-2">Jumlah Biaya</th>
              <th className="border border-gray-300 p-2">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {pengeluaran.map((item, idx) => (
              <tr key={idx}>
                <td className="border border-gray-300 p-2">{item.nama}</td>
                <td className="border border-gray-300 p-2">Rp {item.jumlah}</td>
                <td className="border border-gray-300 p-2 text-center space-x-1">
                  <button className="bg-yellow-500 text-white px-2 py-1 rounded hover:bg-yellow-600" onClick={() => editPengeluaran(idx)}>Edit</button>
                  <button className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600" onClick={() => hapusPengeluaran(idx)}>Hapus</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="text-lg font-semibold">
          <p>Total Penjualan: Rp {totalPenjualan}</p>
          <p>Total Pengeluaran: Rp {totalPengeluaran}</p>
          <p>Laba Bersih: Rp {labaBersih}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
