import React, { useState } from "react";
import jsPDF from "jspdf";
import "jspdf-autotable";

function App() {
  const [penjualan, setPenjualan] = useState([]);
  const [pengeluaran, setPengeluaran] = useState([]);

  const [namaBarang, setNamaBarang] = useState("");
  const [hargaSatuan, setHargaSatuan] = useState("");
  const [jumlahBarang, setJumlahBarang] = useState("");

  const [namaPengeluaran, setNamaPengeluaran] = useState("");
  const [jumlahBiaya, setJumlahBiaya] = useState("");

  const [editPenjualanIdx, setEditPenjualanIdx] = useState(null);
  const [editPengeluaranIdx, setEditPengeluaranIdx] = useState(null);

  // Tambah atau Update Penjualan
  const tambahAtauUpdatePenjualan = () => {
    if (!namaBarang || !hargaSatuan || !jumlahBarang) return;
    const item = { nama: namaBarang, harga: parseInt(hargaSatuan), jumlah: parseInt(jumlahBarang) };

    if (editPenjualanIdx !== null) {
      const updated = [...penjualan];
      updated[editPenjualanIdx] = item;
      setPenjualan(updated);
      setEditPenjualanIdx(null);
    } else {
      setPenjualan([...penjualan, item]);
    }

    setNamaBarang("");
    setHargaSatuan("");
    setJumlahBarang("");
  };

  // Tambah atau Update Pengeluaran
  const tambahAtauUpdatePengeluaran = () => {
    if (!namaPengeluaran || !jumlahBiaya) return;
    const item = { nama: namaPengeluaran, biaya: parseInt(jumlahBiaya) };

    if (editPengeluaranIdx !== null) {
      const updated = [...pengeluaran];
      updated[editPengeluaranIdx] = item;
      setPengeluaran(updated);
      setEditPengeluaranIdx(null);
    } else {
      setPengeluaran([...pengeluaran, item]);
    }

    setNamaPengeluaran("");
    setJumlahBiaya("");
  };

  // Hapus
  const hapusPenjualan = (index) => {
    const updated = [...penjualan];
    updated.splice(index, 1);
    setPenjualan(updated);
  };

  const hapusPengeluaran = (index) => {
    const updated = [...pengeluaran];
    updated.splice(index, 1);
    setPengeluaran(updated);
  };

  // Edit
  const editPenjualan = (index) => {
    const item = penjualan[index];
    setNamaBarang(item.nama);
    setHargaSatuan(item.harga);
    setJumlahBarang(item.jumlah);
    setEditPenjualanIdx(index);
  };

  const editPengeluaran = (index) => {
    const item = pengeluaran[index];
    setNamaPengeluaran(item.nama);
    setJumlahBiaya(item.biaya);
    setEditPengeluaranIdx(index);
  };

  const totalPenjualan = penjualan.reduce(
    (sum, item) => sum + item.harga * item.jumlah,
    0
  );
  const totalPengeluaran = pengeluaran.reduce(
    (sum, item) => sum + item.biaya,
    0
  );
  const labaBersih = totalPenjualan - totalPengeluaran;

  const cetakPDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(18);
    doc.text("NA BAKERY - Ringkasan", 14, 20);

    doc.autoTable({
      startY: 30,
      head: [["Nama Barang", "Harga Satuan", "Jumlah", "Total"]],
      body: penjualan.map((item) => [item.nama, item.harga, item.jumlah, item.harga * item.jumlah]),
      theme: "grid",
    });

    let finalY = doc.lastAutoTable.finalY + 10;

    doc.autoTable({
      startY: finalY,
      head: [["Nama Pengeluaran", "Jumlah Biaya"]],
      body: pengeluaran.map((item) => [item.nama, item.biaya]),
      theme: "grid",
    });

    finalY = doc.lastAutoTable.finalY + 10;

    doc.setFontSize(14);
    doc.text(`Total Penjualan: Rp ${totalPenjualan}`, 14, finalY);
    doc.text(`Total Pengeluaran: Rp ${totalPengeluaran}`, 14, finalY + 7);
    doc.text(`Laba Bersih: Rp ${labaBersih}`, 14, finalY + 14);

    doc.save("NA_BAKERY_Ringkasan.pdf");
  };

  return (
    <div className="p-4 max-w-2xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">NA BAKERY 🍰</h1>

      {/* Form Penjualan */}
      <div className="mb-6 border p-4 rounded">
        <h2 className="text-xl font-semibold mb-2">Tambah Penjualan</h2>
        <input type="text" placeholder="Nama Barang" value={namaBarang} onChange={e => setNamaBarang(e.target.value)} className="border p-1 mr-2" />
        <input type="number" placeholder="Harga Satuan" value={hargaSatuan} onChange={e => setHargaSatuan(e.target.value)} className="border p-1 mr-2" />
        <input type="number" placeholder="Jumlah" value={jumlahBarang} onChange={e => setJumlahBarang(e.target.value)} className="border p-1 mr-2" />
        <button onClick={tambahAtauUpdatePenjualan} className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600">
          {editPenjualanIdx !== null ? "Update" : "Simpan"}
        </button>
      </div>

      {/* Form Pengeluaran */}
      <div className="mb-6 border p-4 rounded">
        <h2 className="text-xl font-semibold mb-2">Tambah Pengeluaran</h2>
        <input type="text" placeholder="Nama Pengeluaran" value={namaPengeluaran} onChange={e => setNamaPengeluaran(e.target.value)} className="border p-1 mr-2" />
        <input type="number" placeholder="Jumlah Biaya" value={jumlahBiaya} onChange={e => setJumlahBiaya(e.target.value)} className="border p-1 mr-2" />
        <button onClick={tambahAtauUpdatePengeluaran} className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600">
          {editPengeluaranIdx !== null ? "Update" : "Simpan"}
        </button>
      </div>

      {/* Ringkasan */}
      <div className="mb-6 border p-4 rounded">
        <h2 className="text-xl font-semibold mb-2">Ringkasan Penjualan</h2>
        <table className="w-full border-collapse border">
          <thead>
            <tr>
              <th className="border px-2">Nama Barang</th>
              <th className="border px-2">Harga Satuan</th>
              <th className="border px-2">Jumlah</th>
              <th className="border px-2">Total</th>
              <th className="border px-2">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {penjualan.map((item, idx) => (
              <tr key={idx}>
                <td className="border px-2">{item.nama}</td>
                <td className="border px-2">{item.harga}</td>
                <td className="border px-2">{item.jumlah}</td>
                <td className="border px-2">{item.harga * item.jumlah}</td>
                <td className="border px-2">
                  <button onClick={() => editPenjualan(idx)} className="bg-yellow-500 text-white px-2 py-1 rounded hover:bg-yellow-600 mr-2">Edit</button>
                  <button onClick={() => hapusPenjualan(idx)} className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600">Hapus</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <h2 className="text-xl font-semibold mt-4 mb-2">Ringkasan Pengeluaran</h2>
        <table className="w-full border-collapse border">
          <thead>
            <tr>
              <th className="border px-2">Nama Pengeluaran</th>
              <th className="border px-2">Jumlah Biaya</th>
              <th className="border px-2">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {pengeluaran.map((item, idx) => (
              <tr key={idx}>
                <td className="border px-2">{item.nama}</td>
                <td className="border px-2">{item.biaya}</td>
                <td className="border px-2">
                  <button onClick={() => editPengeluaran(idx)} className="bg-yellow-500 text-white px-2 py-1 rounded hover:bg-yellow-600 mr-2">Edit</button>
                  <button onClick={() => hapusPengeluaran(idx)} className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600">Hapus</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="mt-4 text-lg font-semibold">
          <p>Total Penjualan: Rp {totalPenjualan}</p>
          <p>Total Pengeluaran: Rp {totalPengeluaran}</p>
          <p>Laba Bersih: Rp {labaBersih}</p>

          <button onClick={cetakPDF} className="bg-green-500 text-white px-4 py-2 rounded mt-2 hover:bg-green-600">
            Cetak PDF
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
